<style type="text/css">:root{--primaryColor:255,100,0;}.webform-container{width:750px;background-color:#fff;font-family:"Roboto",sans-serif;}.webform-header{padding:0 35px;text-align:left;background-color:#fafafa;color:#000;position:relative;height:55px;display:flex;align-items:center;font-weight:600;font-size:14px;}.webform-data{padding:20px 35px 20px;}.webform-data-wrapper{font-size:14px;display:block;text-align:justify;-ms-text-justify:distribute-all-lines;text-justify:distribute-all-lines;white-space:normal;display:flex;flex-wrap:wrap;justify-content:space-between;}.web-form-field{display:inline-block;margin-bottom:30px;position:relative;zoom:1;text-align:left;}.full-field{width:100%;clear:both;}.half-field{vertical-align:top;width:47%;}.web-form-field>label{display:block;max-width:70%;white-space:nowrap;text-overflow:ellipsis;overflow:hidden;font-size:13px;font-weight:400;margin-bottom:5px;}.web-form-field>label.mandatory-field:after{margin-left:2px;content:"*";color:red;}.webform-field-style,input[type=date],input[type=email],input[type=number],input[type=text],select,textarea{width:100%;height:35px;background-color:#fff;border:#ecedee solid 1px;border-radius:3px;display:block;font-size:14px;font-weight:400;padding-left:8px;padding-right:8px;margin:0;outline:0;text-align:left;box-sizing:border-box;-moz-box-sizing:border-box;-webkit-box-sizing:border-box;}.webform-field-style:hover,input[type=date]:hover,input[type=email]:hover,input[type=number]:hover,input[type=text]:hover,select:hover,textarea:hover{border:rgba(var(--primaryColor),.4) solid 1px;}.webform-field-style:focus-within,input[type=date]:focus,input[type=email]:focus,input[type=number]:focus,input[type=text]:focus,select:focus,textarea:focus{border:rgba(var(--primaryColor),.65) solid 1px}.primary-button,.secondary-button{display:inline-flex;align-items:center;justify-content:center;position:relative;vertical-align:top;font-size:13px;cursor:pointer;padding:0 15px;margin-right:10px;outline:0;border-radius:25px;-moz-border-radius:25px;-webkit-border-radius:25px;height:28px;box-sizing:border-box;-moz-box-sizing:border-box;white-space:nowrap;overflow:hidden;text-overflow:ellipsis;max-width:250px;transition:.3s;line-height:1.5;flex:0 0 auto}.primary-button{color:#fff;background-color:rgb(var(--primaryColor));border:rgb(var(--primaryColor)) solid 1px}.secondary-button{color:rgb(var(--primaryColor));background:#fff;border:rgb(var(--primaryColor)) solid 1px}.attachment-title{height:35px;width:100%;position:relative;padding:10px;display:flex;align-items:center;border:#ecedee dashed 1px;background-color:#fff;border-radius:3px;box-sizing:border-box;-moz-box-sizing:border-box;-webkit-box-sizing:border-box}.attachment-title .attachment-msg{width:250px;white-space:nowrap;overflow:hidden;color:#777}.attachment-title .attachment-input{position:absolute;top:0;bottom:0;left:0;right:0;opacity:0;cursor:pointer;width:100%}.attachment-title:hover .attachment-msg{color:rgb(var(--primaryColor))}.attachment{padding:0 15px;background:#fff;border:#ecedee solid 1px;border-radius:3px;position:relative;margin-top:20px;width:100%;height:35px;line-height:35px;box-sizing:border-box;-moz-box-sizing:border-box;-webkit-box-sizing:border-box}.attachment:before{content:"X";position:absolute;display:flex;align-items:center;justify-content:center;font-size:9px;right:10px;top:9px;background:rgb(var(--primaryColor));width:16px;height:16px;color:#fff;cursor:pointer;border-radius:2px}.add-attachment{float:left;background:#fff;border-radius:2px;cursor:pointer;color:#30a66b;padding:8px 17px;margin-right:11px;border:1px solid}.webform-select-style{position:relative;display:block;width:100%;padding-left:0;padding-right:0}.webform-select-style.hide{display:none}.webform-select-style select{width:100%;height:100%;border:none;box-shadow:none;background:0 0;background-image:none;-webkit-appearance:none;color:#000;-moz-appearance:none;appearance:none}.webform-select-style:after{content:"";background:none repeat scroll 0 0 transparent;height:7px;position:absolute;width:7px;margin-top:1px;border-width:1px 1px 0 0;box-shadow:1.5px 1.5px 0 rgb(var(--primaryColor));transform:rotate(43deg);float:right;right:10px;top:10px;cursor:pointer;pointer-events:none}.webform-select-style[disabled]:after{cursor:not-allowed}.webform-select-style select:focus{outline:0}.webform-select-style select option{float:left;overflow:hidden;text-overflow:ellipsis;width:100%;padding:0 8px}.webform-select-style select::-ms-expand{display:none}.webform-field-style{display:flex;align-items:center}.webform-field-style>input[type=text]{border:0!important;padding:0!important;height:100%!important;flex:1}.webform-field-style>.web-captcha{flex:0 0 auto;width:200px;height:100%;display:inline-flex;align-items:center;border-left:#ecedee solid 1px}.webform-field-style>.web-captcha canvas{ border:0;outline:0;user-select:none;pointer-events:none; margin-left:20px;}.webform-field-style>.web-captcha>.captcha-refresh{margin-left:auto;font-size:28px;cursor:pointer;color:rgb(var(--primaryColor));position:relative;top:-2px;padding-left:10px;}</style><script type="text/javascript">var code;window.onload = generateCaptcha;function generateCaptcha(){document.getElementById("captcha").innerHTML = "";var charsArray ="0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ@!#$%"; var lengthOtp = 6;var captcha = [];for (var i = 0; i < lengthOtp; i++) {var index = Math.floor(Math.random() * charsArray.length + 1);if (captcha.indexOf(charsArray[index]) == -1)captcha.push(charsArray[index]);else i--;}var canv = document.createElement("canvas");canv.id = "captcha";canv.width = 100;canv.height = 50;var ctx = canv.getContext("2d");ctx.font = "25px Georgia";ctx.strokeText(captcha.join(""), 0, 30);code=captcha.join("");document.getElementById("captcha").appendChild(canv);}  function addAttachment(){var fileInput = document.getElementById("uploadfile");   var filename = fileInput.files[0].name; document.getElementById("filename").innerHTML = filename;document.getElementById("filename").style.display = "block";}function removeAttachement(){document.getElementById("uploadfile").value = ""; document.getElementById("filename").value = "";document.getElementById("filename").style.display = "none";}function validateForm(){if(document.getElementById("uploadfile").value != "") { var fsize = document.getElementById("uploadfile").files[0].size / 1024 / 1024; if (fsize > 125) {alert("File size exceeds 125 MB");return false;}}var formName=document.fileabugform.subject.value;var eMail=document.fileabugform.email.value.trim();document.fileabugform.email.value = eMail;if (formName==null || formName==""){alert("Please enter Issue name");return false;}if(eMail==null || eMail==""){alert("Please enter a valid email address");return false;}var atpos=eMail.indexOf("@");var dotpos=eMail.lastIndexOf(".");if (atpos<1 || dotpos<atpos+2 || dotpos+2>=eMail.length)  {  alert("Please enter a valid email address");  return false;  }if(document.getElementById("captcha")){if(code!= document.getElementById("input_captcha").value){ generateCaptcha();alert("Invalid Captcha");document.getElementById("input_captcha").value="";return false;}} var customfields = document.getElementsByClassName("customFields");for(i=0;i<customfields.length;i++){var name=customfields[i].getAttribute("name");var label = customfields[i].getAttribute("data-lable-name");if(name.startsWith("UDF_NDATE") && customfields[i].value!=null && customfields[i].value!=""){if (customfields[i].value.match(/^(?:(0[1-9]|1[012])[\- \/.](0[1-9]|[12][0-9]|3[01])[\- \/.](19|20)[0-9]{2})$/)){return true;}else{alert("Please enter a valid date");return false;}}if(name.startsWith("UDF_NLONG") && customfields[i].value != null && customfields[i].value != ""){if (customfields[i].value.match(/^[0-9]\d*$/)) {return true;} else {alert("Please enter numeric value for "+label+"");return false;}}if(customfields[i].getAttribute("data-mand")=="true" && customfields[i].value==""){alert("The mandatory fields cannot be empty");return false;}} var dueDate = document.getElementById("dueDate").value;if(dueDate!="null" || dueDate!=""){if (dueDate.match(/^(?:(0[1-9]|1[012])[\- \/.](0[1-9]|[12][0-9]|3[01])[\- \/.](19|20)[0-9]{2})$/)){return true;}else{alert("Please enter a valid date");return false;}}  } </script><div class="webform-container"><form name="fileabugform" action="https://projects.zoho.com/portal/iworklab/addbugfromiframe.do" method="post" enctype="multipart/form-data" onSubmit="return validateForm()"><input type="hidden" name="projId" value="projects-3d36a09530afca3d7c466409499145d951c23ef8ca32e0457091f5a807205d1a"/><input type="hidden" name="fId" value="projects-24245dd0590ce4da4e9349a1fbd21e5ab3c089ebe9e766bbfa7c22dddcdd4419"/><div class="webform-header">Submit Issue</div><div class="webform-data"><div class="webform-data-wrapper"><div class="web-form-field full-field"><label class="mandatory-field">Issue Title</label><input type="text" id="title" name="subject" value=""></div><div class="web-form-field full-field"><label>Issue Description</label><textarea id="description" name="desc" rows="4" style="height: 80px;"></textarea></div><div class="web-form-field full-field"><label>Attach File</label><div class="attachment-title"> <span class="attachment-msg">Drop files or add attachments here...</span><input type="file" id="uploadfile" name="uploadfile" onchange="addAttachment();" class="attachment-input"></div><div style="margin-top:20px;display:none" class="attachment" id="filename" onclick="removeAttachement();">-</div></div><div class="web-form-field half-field"><label class="mandatory-field">Email Address</label><input type="text" name="email"></div><div class="web-form-field half-field"><label>Severity</label><div class="webform-select-style"><select name="Severity" style=" width: 100%; border-color: #e7e7e7; border-style: solid; border-width: 1px; color: #3A3A3A; font-size: 13px; font-weight: normal; border-radius:3px; padding:3px; height: 35px; outline:none; background-color: #fff;"><option value="projects-47db7f07c4419de68991723e5de990fa8d482a5278a7a69511db556a43852e26">None</option><option value="projects-11730bea01ed3025b9ac4854dbeacff9f281990fdd5df6943b7ea0e45bcc72be">Show stopper</option><option value="projects-11730bea01ed3025b9ac4854dbeacff905b27b96463567f35a176bd15bd39d58">Critical</option><option value="projects-11730bea01ed3025b9ac4854dbeacff99f1e134fd2718901235c301cb35315b0">Major</option><option value="projects-11730bea01ed3025b9ac4854dbeacff90ab9fa9da56e3bf62387af56bb582073">Minor</option></select></div></div><div class="web-form-field half-field"><label>Reproducible</label><div class="webform-select-style"><select style=" width: 100%; border-color: #e7e7e7; border-style: solid; border-width: 1px; color: #3A3A3A; font-size: 13px; font-weight: normal; /* border-radius:3px; */ padding:3px; outline:none; background-color: #fff; height: 35px;" name="Is it Reproducible"><option value="projects-47db7f07c4419de68991723e5de990faeeb22735195c2ce7c385b68e18a624e8">None</option><option value="projects-3c13f5ec12ff13acbdd2ae2fe0b997ddd0f7403c4dd30ee595e13bb06bc9644c">Always</option><option value="projects-3c13f5ec12ff13acbdd2ae2fe0b997dd823f2f87d754b8f646fd58cc7691d508">Sometimes</option><option value="projects-3c13f5ec12ff13acbdd2ae2fe0b997dd3336f944d5c22a60f81a1698070aa3be">Rarely</option><option value="projects-3c13f5ec12ff13acbdd2ae2fe0b997dddf95d2844bb7c8752d8baa7ce838d0c4">Unable</option><option value="projects-3c13f5ec12ff13acbdd2ae2fe0b997ddf574f3f409097d53f8d7fcce1485e5d9">Never tried</option><option value="projects-3c13f5ec12ff13acbdd2ae2fe0b997dd08eaf5eb1e6fc55fc563574c81a76733">Not applicable</option></select></div></div><div class="web-form-field half-field"><label class="mandatory-field">Platform(s)</label><select name="UDF_NCHAR3" style="border-color: #e7e7e7; border-style: solid; border-width: 1px; color: #3A3A3A; font-size: 13px; font-weight: normal; border-radius:3px; padding:3px; height: 35px; outline:none; background-color: #fff;" data-mand="true" fieldtype="picklist" data-lable-name="Platform(s)"><option value="">--Select--</option><option value="iOS">iOS</option><option value="Android">Android</option><option value="Website">Website</option><option value="Windows">Windows</option><option value="Linux">Linux</option><option value="Mac">Mac</option></select></div><div class="web-form-field full-field"><label>Enter Captcha</label> <div class="webform-field-style"><input type="text" id="input_captcha"><span class="web-captcha"><span id="captcha"><canvas id="captcha" width="100" height="50"></canvas></span><span style=" font-family: Lucida Sans Unicode"class="captcha-refresh " onclick="generateCaptcha();">↻</span></span></div></div><div class="web-form-field full-field"><div style="line-height: 20px; color: #ff4d47;">Note: Email ID is used to know the reporter of this bug. Submitting your email ID will not add you as a user or give you access to portal data.</div></div><div style="display:flex; align-items: center; width:100%;"><button type="submit" value="Save" title="Save" class="primary-button">Save</button><input type="reset" value="Cancel" title="Cancel" class="secondary-button"></div></div></div></form></div>
